import React from 'react';
import {
  addedDeviceType,
  setAddedDeviceActionType,
} from '../store/reducer/deviceReducer';
import { useForm, Controller } from 'react-hook-form';
import { makeStyles } from '@material-ui/core/styles';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import IconButton from '@material-ui/core/IconButton';
import ArrowForwardSharpIcon from '@material-ui/icons/ArrowForwardSharp';
import ArrowBackSharpIcon from '@material-ui/icons/ArrowBackSharp';
import Dropzone from 'react-dropzone';
import Paper from '@material-ui/core/Paper';
import CloudUpload from '@material-ui/icons/CloudUpload';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InsertDriveFile from '@material-ui/icons/InsertDriveFile';
import Button from '@material-ui/core/Button';

//-----------------------------------------
// типизация пропсов
type PropsType = {
  addedDevice: addedDeviceType;
  setAddedDevice: (data: addedDeviceType) => setAddedDeviceActionType;
  handleNext: () => void;
  handleBack: () => void;
};
//--------------------------------------------
const schema = yup.object().shape({
  picture: yup
    .mixed()
    .test('required', 'You need to provide a file', (value) => {
      return value && value.length;
    }),
  // picture: yup.mixed().required('First name is a required field'),
  //  .test('fileSize', 'The file is too large', (value) => {
  //   return value && value[0].size <= 20000;
  //  }),
});
const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: '#eee',
    textAlign: 'center',
    cursor: 'pointer',
    color: '#333',
    padding: '10px',
    marginTop: '20px',
  },
  icon: {
    marginTop: '16px',
    color: '#888888',
    fontSize: '42px',
  },
  buttons: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: 30,
  },
}));

const Step3: React.FC<PropsType> = ({
  addedDevice,
  setAddedDevice,
  handleNext,
  handleBack,
}) => {
  const styles = useStyles();
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({
    mode: 'onBlur',
    resolver: yupResolver(schema),
  });

  const onSubmit = (data: any) => {
    console.log(data);
  };
  console.log(errors);
  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Controller
        control={control}
        name="picture"
        defaultValue={addedDevice.picture}
        render={({ field: { onChange, onBlur, value, name } }) => (
          <>
            <Dropzone onDrop={onChange}>
              {({ getRootProps, getInputProps }) => (
                <Paper
                  variant="outlined"
                  className={styles.root}
                  {...getRootProps()}
                >
                  <CloudUpload className={styles.icon} />
                  <input
                    {...getInputProps()}
                    name={name}
                    // onBlur={onBlur}
                    // required
                  />
                  <p>Drag 'n' drop files here, or click to select files</p>
                </Paper>
              )}
            </Dropzone>
            <List>
              {value.map((f: any, index: any) => (
                <ListItem key={index}>
                  <ListItemIcon>
                    <InsertDriveFile />
                  </ListItemIcon>
                  <ListItemText primary={f.name} secondary={f.size} />
                </ListItem>
              ))}
            </List>
          </>
        )}
      />
      {errors.picture && <p>{errors.picture.message}</p>}
      <div className={classes.buttons}>
        <IconButton style={{ color: '#3f51b5' }} onClick={handleBack}>
          <ArrowBackSharpIcon />
        </IconButton>
        <IconButton type="submit" style={{ color: '#3f51b5' }}>
          <ArrowForwardSharpIcon />
        </IconButton>
      </div>
    </form>
  );
};
export default Step3;
