import React from 'react';

import { DeviceType } from '../store/reducer/deviceReducer';
import { ROOT_URL } from '../constants/url';
import { useHistory } from 'react-router-dom';

import {
  Theme,
  createStyles,
  makeStyles,
  useTheme,
} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import SkipPreviousIcon from '@material-ui/icons/SkipPrevious';
import PlayArrowIcon from '@material-ui/icons/PlayArrow';
import SkipNextIcon from '@material-ui/icons/SkipNext';
import { Button, Divider } from '@material-ui/core';

//типизация----------------------

type PropsType = {
  item: DeviceType;
  removeDevice: (id: string | undefined) => void;
};
//---------------------------------

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
    },
    details: {
      display: 'flex',
      flexDirection: 'column',
    },
    content: {
      flex: '1 0 auto',
    },
    cover: {
      width: 151,
    },
    media: {
      height: 150,
    },
    controls: {
      display: 'flex',
      alignItems: 'center',
      paddingLeft: theme.spacing(1),
      paddingBottom: theme.spacing(1),
    },
    playIcon: {
      height: 38,
      width: 38,
    },
  })
);

const DeleteDevice: React.FC<PropsType> = ({ item, removeDevice }) => {
  const { name, picture, price } = item;
  const classes = useStyles();
  const theme = useTheme();
  const history = useHistory();

  return (
    <>
      <Card className={classes.root} key={item._id}>
        <CardMedia
          children={
            <img
              src={`${ROOT_URL}/${picture[0]}`}
              style={{ height: '150px', width: 'auto' }}
            />
          }
          className={classes.media}
          title={name}
          onClick={() => {
            history.push(`/profileDevice/${item._id}`);
          }}
        />
        <div className={classes.details}>
          <CardContent className={classes.content}>
            <Typography component="h5" variant="h5">
              Live From Space
            </Typography>
            <Typography variant="subtitle1" color="textSecondary">
              Mac Miller
            </Typography>
          </CardContent>
          <div className={classes.controls}>
            <Button
              variant="contained"
              color="secondary"
              onClick={() => removeDevice(item._id)}
            >
              Удалить
            </Button>
          </div>
        </div>
      </Card>
      <Divider />
    </>
  );
};

export default DeleteDevice;

