import React from 'react';

import { DeviceType } from '../store/reducer/deviceReducer';
import { ROOT_URL } from '../constants/url';
import { useHistory } from 'react-router-dom';

import {
  Theme,
  createStyles,
  makeStyles,
  useTheme,
} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import { Button, Divider } from '@material-ui/core';

//типизация----------------------

type PropsType = {
  item: DeviceType;
  removeDevice: (id: string | undefined) => void;
};
//---------------------------------

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      padding: 15,
    },

    content: {
      flex: '1 0 auto',
    },

    media: {
      height: 150,
      cursor: 'pointer',
    },
  })
);

const DeleteDevice: React.FC<PropsType> = ({ item, removeDevice }) => {
  const { name, picture, price } = item;
  const classes = useStyles();
  const theme = useTheme();
  const history = useHistory();

  return (
    <>
      <Card key={item._id}>
        <div className={classes.root}>
          <CardMedia
            children={
              <img
                src={`${ROOT_URL}/${picture[0]}`}
                style={{ height: '100px', width: 'auto' }}
              />
            }
            className={classes.media}
            title={name}
            onClick={() => {
              history.push(`/profileDevice/${item._id}`);
            }}
          />

          <CardContent className={classes.content}>
            <Typography component="h5" variant="h5">
              Live From Space
            </Typography>
            <Typography variant="subtitle1" color="textSecondary">
              Mac Miller
            </Typography>
          </CardContent>
        </div>
        <Button
          variant="outlined"
          color="secondary"
          fullWidth={false}
          onClick={() => removeDevice(item._id)}
        >
          Удалить
        </Button>
      </Card>

      <Divider />
    </>
  );
};

export default DeleteDevice;

