import axios from 'axios';
import { ThunkAction } from 'redux-thunk'; // для типизации санки
import { RootStateType } from '../store/store'; //типизация всего стора
import { ModelUrls } from '../constants/url';
import { DeviceAtionType } from '../store/reducer/deviceReducer'; //типизация экшенов устройств
import { setDevices } from '../store/reducer/deviceReducer';

// типизация санки
export type ThunkType = ThunkAction<
  Promise<void>,
  RootStateType,
  unknown, //extraArgument
  DeviceAtionType
>;
// авторизация
export const getDevices = (
  typeId: string | null,
  brandId: string | null,
  currentPage: number | null
): ThunkType => {
  return async (dispatch) => {
    try {
      const response = await axios.get(
        `${ModelUrls.DEVICE}
      ?typeId=${typeId}
      &brandId=${brandId}
      &limit=6
      &page=${currentPage}`
      );
      console.log(1);
      console.log(response);
      //запись в стейт
      // dispatch(setDevices(data.device));
    } catch (e: any) {}
  };
};
