import React, { useState } from 'react';
import { makeStyles, Theme } from '@material-ui/core/styles';
import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import Collapse from '@material-ui/core/Collapse';

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    marginTop: 50,
  },
  list: { paddingLeft: theme.spacing(4) },
  nested: {
    paddingLeft: theme.spacing(9),
  },
}));

const TypeBar: React.FC = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(false); // для вложенного списа
  const [indexActiv, setIndexActiv] = useState<null | number>(null); //чтобы не раскрывался весь список

  const handleClick = (index: number | null) => {
    setIndexActiv(index);
    setOpen(!open);
  }; // управление вложенным списком

  console.log(indexActiv);
  console.log(open);
  const brand = ['Samsung', 'Lenovo', 'Acer'];
  const device = ['Смартфоны', 'Планшеты', 'Ноутбуки'];
  return (
    <List
      component="nav"
      className={classes.root}
      subheader={
        <ListSubheader component="div">Nested List Items</ListSubheader>
      }
    >
      {device.map((item, index) => {
        return (
          <div key={item}>
            <ListItem
              className={classes.list}
              button
              onClick={() => handleClick(index)}
            >
              <ListItemText
                disableTypography
                primary={<Typography>{item}</Typography>}
              />
              {open ? <ExpandLess /> : <ExpandMore />}
            </ListItem>
            <Divider />
            <Collapse
              in={indexActiv === index && open}
              timeout="auto"
              unmountOnExit
            >
              <List component="div">
                {brand.map((el) => {
                  return (
                    <div>
                      <ListItem button key={el} className={classes.nested}>
                        <ListItemText
                          disableTypography
                          primary={<Typography>{el}</Typography>}
                        />
                      </ListItem>
                      <Divider />
                    </div>
                  );
                })}
              </List>
            </Collapse>
          </div>
        );
      })}
    </List>
  );
};

export default TypeBar;

