import React, { useState } from 'react';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import MenuIcon from '@material-ui/icons/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import Container from '@material-ui/core/Container';
import Divider from '@material-ui/core/Divider';
import Typography from '@material-ui/core/Typography';
import { makeStyles, Theme } from '@material-ui/core/styles';
import { Link } from 'react-router-dom';
//----типизация пропсов----
type PropsType = {};
//-------------------------
const useStyles = makeStyles((theme: Theme) => ({
  menu: {
    marginTop: '50px',
  },
  link: {
    textDecoration: 'none',
    color: 'initial',
  },
}));

const MenuBar: React.FC<PropsType> = ({}) => {
  const classes = useStyles();
  // для меню
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const [open, setOpen] = React.useState(false);
  const handleOpenMenu = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
    setOpen(!open);
  };
  const handleClose = () => {
    setAnchorEl(null);
    setOpen(!open);
  };
  //--------------------------
  return (
    <>
      <Container maxWidth="md">
        <List component="nav" style={{ display: 'flex', margin: '0 auto' }}>
          <ListItem
            button
            onMouseEnter={handleOpenMenu}
            onMouseLeave={handleClose}
          >
            <ListItemText
              disableTypography
              primary={
                <Typography align="center">
                  <MenuIcon
                    style={{
                      display: 'initial-flex',
                      verticalAlign: 'middle',
                      marginRight: '10px',
                    }}
                  />
                  Каталог
                </Typography>
              }
            />
          </ListItem>

          <ListItem button>
            <ListItemText
              primary={
                <Typography align="center">О компании</Typography>
              } /* secondary={options[selectedIndex]} */
            />
          </ListItem>
          <ListItem button>
            <ListItemText
              primary={
                <Typography align="center">Доставка</Typography>
              } /* secondary={options[selectedIndex]} */
            />
          </ListItem>
          <ListItem button>
            <ListItemText
              primary={
                <Typography align="center">Оплата</Typography>
              } /* secondary={options[selectedIndex]} */
            />
          </ListItem>
        </List>
      </Container>
      <Divider />
      <Menu
        id="menu"
        anchorEl={anchorEl}
        keepMounted
        open={open}
        className={classes.menu}
        onClose={handleClose}
      >
        <MenuItem onClick={handleClose}>
          {' '}
          <Link to="/" className={classes.link}>
            To do list
          </Link>
        </MenuItem>
        <MenuItem onClick={handleClose}>
          <Link to="/posts" className={classes.link}>
            Posts
          </Link>
        </MenuItem>
      </Menu>
    </>
  );
};

export default MenuBar;
