import React, { useState } from 'react';
import {
  useForm,
  SubmitHandler,
  Controller,
  useFieldArray,
} from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import IconButton from '@material-ui/core/IconButton';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import Button from '@material-ui/core/Button';

//типизация данных
type InfoType = {
  title: string;
  description: string;
  id?: number;
};

//схема валидации---------------------
const schema = yup.object().shape({
  title: yup.string().required('Поле обязательное для заполнения'),
  description: yup.string().required('Поле обязательное для заполнения'),
});
//-----------------------------------------

const DeviceProperty = () => {
  /*  const [info, setInfo] = useState<InfoType[]>([]);

  const addInfo = (): void => {
    setInfo([...info, { title: '', description: '', id: Date.now() }]);
  };

  const removeInfo = (id: number | undefined): void => {
    setInfo(info.filter((item) => item.id !== id));
  }; */

  const {
    handleSubmit,
    control,
    watch,
    formState: { errors },
    register,
    reset,
    trigger,
  } = useForm({
    defaultValues: {
      test: [{ title: 'Bill', description: 'Luo' }],
    },
    resolver: yupResolver(schema),
    mode: 'onBlur',
  });
  // получение данных из формы и отправка на сервак(авторизация или регистрация)
  const onSubmit: SubmitHandler<InfoType[]> = (data: InfoType[]): void => {
    console.log('Отправлено:', data);
  };
  /*  const changeInfo = (
    key: string,
    id: number | undefined,
    value: any
  ): void => {
    console.log(key);
    console.log(watch('title'));
    setInfo(info.map((i) => (i.id === id ? { ...i, [key]: value } : i)));
  };
  const onCl = (): void => {
    console.log(info);
  };
  const title = watch('title');
  const description = watch('description'); */

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'test',
  });

  console.log(fields);
  console.log(remove);

  return (
    <React.Fragment>
      {/* <Button
        color="primary"
        style={{ marginBottom: 15 }}
        //  onClick={addInfo}
        //className={classes.button}
      >
        Добавить новое свойство
      </Button> */}
      <form noValidate onSubmit={handleSubmit(onSubmit)}>
        {fields.map((item, index) => {
          return (
            <Grid container spacing={3} key={item.id}>
              <Grid item xs={12} sm={3}>
                <Controller
                  name={`test.${index}.title`}
                  control={control}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      required
                      fullWidth
                      label=" название "
                      //  error={!!errors.title}
                      //   helperText={errors.title ? errors.title?.message : null}
                    />
                  )}
                />
              </Grid>
              <Grid item xs={12} sm={7}>
                <Controller
                  name={`test.${index}.description`}
                  control={control}
                  render={({ field }) => (
                    <TextField
                      {...field}
                      required
                      fullWidth
                      label=" описание "
                      multiline
                      //  error={!!errors.description}
                      //  helperText={
                      //     errors.description ? errors.description?.message : null
                      //    }
                    />
                  )}
                />
              </Grid>
              <Grid item xs={12} sm={2}>
                <IconButton onClick={() => () => remove(index)}>
                  <HighlightOffIcon style={{ fontSize: 30 }} />
                </IconButton>
              </Grid>
            </Grid>
          );
        })}
        {/*  <Button onClick={onCl}>Отправить</Button> */}
        <button
          type="button"
          onClick={() => append({ title: '', description: '' })}
        >
          append
        </button>
        <input type="submit" />
      </form>
    </React.Fragment>
  );
};
export default DeviceProperty;
