import React, { useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import TypeBar from '../components/TypeBar';
import DeviceContainer from '../components/DeviceContainer';
import { RootStateType } from '../store/store'; //типизиция всего стора
import {
  setTypeId,
  setBrandId,
  setCurrentPage,
} from '../store/reducer/deviceReducer'; //запись выбранного типа и брэнда устройств
import { getDevices } from '../action/deviceAction'; //запрос на получение устройств
import { connect } from 'react-redux';

//типизация--------------------------------
type MapStateToPropsType = {};
type MapDispathPropsType = {
  getDevices: (
    typeId: string | null,
    brandId: string | null,
    limit: number,
    currentPage: number | null
  ) => void;
  setTypeId: (data: string) => void;
  setBrandId: (data: string) => void;
};
type PropsType = MapDispathPropsType & MapStateToPropsType;
//-----------------------------------------

const Content: React.FC<PropsType> = ({
  getDevices,
  setTypeId,
  setBrandId,
}) => {
  useEffect(() => {
    getDevices(null, null, 5, 1);

    // eslint-disable-next-line
  }, []);
  return (
    <Grid item container>
      <TypeBar />
      <DeviceContainer />
    </Grid>
  );
};
const mapStateToProps = (state: RootStateType): MapStateToPropsType => {
  return {};
};
export default connect<
  MapStateToPropsType,
  MapDispathPropsType,
  unknown, // личные пропсы
  RootStateType
>(mapStateToProps, { getDevices, setTypeId, setBrandId })(Content);
