import React, { useState } from 'react';
import { makeStyles, Theme } from '@material-ui/core/styles';
import Masonry from 'react-masonry-css'; // контейнерный компонент для стиля(расположение элементов)
import Device from './Device';
import { Grid } from '@material-ui/core';

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}));

const devices = [
  {
    _id: '616c6cd76a37991eed102c32',
    name: 'Samsung S10',
    price: 1300,
    picture: 'http://localhost:8000/ee454e3b-b601-428f-9072-fa5b14868c49.jpg',
  },
  {
    _id: '616c6cd76a37991eed102c32',
    name: 'Samsung S10 hfuihiuwehiwehiwehgvwioegjwoiegjowie',
    price: 1300,
    picture: 'http://localhost:8000/ee454e3b-b601-428f-9072-fa5b14868c49.jpg',
  },
  {
    _id: '616c6cd76a37991eed102c32',
    name: 'Samsung S10 hfbjhgerkgekrjgerigj',
    price: 1300,
    picture: 'http://localhost:8000/ee454e3b-b601-428f-9072-fa5b14868c49.jpg',
  },
  {
    _id: '616c6cd76a37991eed102c32',
    name: 'Samsung S10 hbejvjrvhj',
    price: 1300,
    picture: 'http://localhost:8000/ee454e3b-b601-428f-9072-fa5b14868c49.jpg',
  },
];
const breakpoints = {
  default: 4,
  1100: 2,
  700: 1,
}; // это для Masonry,компонент регулирет расположение карточек(отдельно установлен)
const Devices: React.FC = () => {
  return (
    <Grid item xs={12} sm={10}>
      <Masonry
        breakpointCols={breakpoints}
        className="my-masonry-grid"
        columnClassName="my-masonry-grid_column"
      >
        {devices.map((item, index) => {
          return <Device item={item} />;
        })}
      </Masonry>
    </Grid>
  );
};

export default Devices;
