import React from 'react';
import { useForm, SubmitHandler, Controller } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import IconButton from '@material-ui/core/IconButton';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import Button from '@material-ui/core/Button';

//типизация данных
type InfoType = {
  title: string;
  description: string;
};

//схема валидации---------------------
const schema = yup.object().shape({});
//-----------------------------------------

const DeviceProperty = () => {
  const {
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
    mode: 'onChange',
  });
  // получение данных из формы и отправка на сервак(авторизация или регистрация)
  const onSubmit: SubmitHandler<InfoType[]> = (data: InfoType[]): void => {
    console.log('Отправлено:', data);
  };
  return (
    <React.Fragment>
      <Button
        color="primary"
        style={{ marginBottom: 15 }}

        //className={classes.button}
      >
        Добавить новое свойство
      </Button>
      <form noValidate onSubmit={handleSubmit(onSubmit)}>
        <Grid container spacing={3}>
          <Grid item xs={12} sm={3}>
            <Controller
              name="title"
              control={control}
              defaultValue=""
              render={({ field }) => (
                <TextField
                  {...field}
                  required
                  fullWidth
                  id="title"
                  label=" название "
                  autoFocus
                  error={!!errors.title}
                  helperText={errors.title ? errors.title?.message : null}
                />
              )}
            />
          </Grid>
          <Grid item xs={12} sm={7}>
            <Controller
              name="description"
              control={control}
              defaultValue=""
              render={({ field }) => (
                <TextField
                  {...field}
                  required
                  fullWidth
                  id="description"
                  label=" описание "
                  multiline
                  error={!!errors.description}
                  helperText={
                    errors.description ? errors.description?.message : null
                  }
                />
              )}
            />
          </Grid>
          <Grid item xs={12} sm={2}>
            <IconButton>
              <HighlightOffIcon style={{ fontSize: 30 }} />
            </IconButton>
          </Grid>
        </Grid>
        <Button
          type="submit"
          // disabled={!isValid || !isDirty}
        >
          Отправить
        </Button>
      </form>
    </React.Fragment>
  );
};
export default DeviceProperty;
