import React, { useState } from 'react';
import { makeStyles, Theme } from '@material-ui/core/styles';
import Masonry from 'react-masonry-css'; // контейнерный компонент для стиля(расположение элементов)
import Device from './Device';
import Grid from '@material-ui/core/Grid';
import {
  DeviceType,
  setCurrentPageActionType,
} from '../store/reducer/deviceReducer';
import PaginationButtons from './PaginationButtons';
import { count } from 'console';

//----типизация пропсов----
type PropsType = {
  devices: DeviceType[];
  totalCount: number;
  limit: number;
  page: number;
  setCurrentPage: (data: number) => setCurrentPageActionType;
};
//-------------------------

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}));

const breakpoints = {
  default: 4,
  1100: 2,
  700: 1,
}; // это для Masonry,компонент регулирет расположение карточек(отдельно установлен)
const DeviceContainer: React.FC<PropsType> = ({
  devices,
  totalCount,
  limit,
  page,
  setCurrentPage,
}) => {
  let countPage: number = Math.ceil(totalCount / limit); //для пагинации,расчёт количества страниц
  return (
    <Grid item xs={12} sm={10}>
      <Masonry
        breakpointCols={breakpoints}
        className="my-masonry-grid"
        columnClassName="my-masonry-grid_column"
      >
        {devices.map((item) => {
          return <Device item={item} key={Math.random()} />;
        })}
      </Masonry>
      {!!countPage && (
        <PaginationButtons
          setCurrentPage={setCurrentPage}
          countPage={countPage}
          page={page}
        />
      )}
    </Grid>
  );
};

export default DeviceContainer;
