import React from 'react';
import { useForm, SubmitHandler, Controller } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { MenuItem } from '@material-ui/core';

import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { useHistory } from 'react-router-dom';
import { TypeDeviceType, BrandType } from '../store/reducer/deviceReducer';

//типизация данных
type InfoType = {
  title: string;
  discription: string;
};

type DataDeviceType = {
  name: string;
  price: number;
  picture: any;
  info: InfoType[];
  typeId: string;
  brandId: string;
};

//схема валидации---------------------
const schema = yup.object().shape({});
//-----------------------------------------
// типизация пропсов
type PropsType = {
  types: TypeDeviceType[];
  brands: BrandType[];
};
//--------------------------------------------

const useStyles = makeStyles((theme) => ({
  form: {
    width: '100%',
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  link: {
    pointerEvents: 'none',
  },
  paper: {
    margin: theme.spacing(4, 4),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  },
}));

const FormDevice: React.FC<PropsType> = ({ types, brands }) => {
  const history: any = useHistory();
  const classes = useStyles();
  const {
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
    mode: 'onChange',
  });
  // получение данных из формы и отправка на сервак(авторизация или регистрация)
  const onSubmit: SubmitHandler<DataDeviceType> = (
    data: DataDeviceType
  ): void => {
    console.log('Отправлено:', data);
  };

  return (
    <form noValidate className={classes.form} onSubmit={handleSubmit(onSubmit)}>
      <Controller
        name="types"
        control={control}
        defaultValue=""
        render={({ field }) => (
          <TextField
            {...field}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="type"
            label="Выберите тип"
            autoComplete="text"
            autoFocus
            error={!!errors.types}
            select
            helperText={errors.types ? errors.types?.message : null}
          >
            {types.map((item, index) => {
              return (
                <MenuItem value={item._id} key={index}>
                  {item.name}
                </MenuItem>
              );
            })}
          </TextField>
        )}
      />

      <Controller
        name="brands"
        control={control}
        defaultValue=""
        render={({ field }) => (
          <TextField
            {...field}
            variant="outlined"
            margin="normal"
            required
            fullWidth
            id="brands"
            label="Выберите брэнд"
            autoComplete="text"
            autoFocus
            error={!!errors.brands}
            select
            helperText={errors.brands ? errors.brands?.message : null}
          >
            {brands.map((item, index) => {
              return (
                <MenuItem value={item._id} key={index}>
                  {item.name}
                </MenuItem>
              );
            })}
          </TextField>
        )}
      />

      <Button
        className={classes.submit}
        variant="contained"
        color="primary"
        fullWidth
        type="submit"
        // disabled={!isValid || !isDirty}
      >
        Отправить
      </Button>
    </form>
  );
};
export default FormDevice;
