import React, { useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import TypeBar from '../components/TypeBar';
import DeviceContainer from '../components/DeviceContainer';
import { RootStateType } from '../store/store'; //типизиция всего стора
import {
  setTypeId, //запись выбранного типа устройства
  setBrandId, //запись выбранного  брэнда устройства
  setCurrentPage, //запись текущей страницы
} from '../store/reducer/deviceReducer';
import {
  setCurrentPageActionType,
  setTypeIdActionType,
  setBrandIdActionType,
} from '../store/reducer/deviceReducer'; // типизация экшенов
import { DeviceType, TypeDeviceType } from '../store/reducer/deviceReducer'; //типизация данных
import { getDevices, getTypes } from '../action/deviceAction'; //запрос на получение устройств  и типов устройств
import { connect } from 'react-redux';

//типизация--------------------------------
type MapStateToPropsType = {
  devices: DeviceType[];
  types: TypeDeviceType[];
  totalCount: number;
  limit: number;
  page: number;
  typeId: string | null;
  brandId: string | null;
};
type MapDispathPropsType = {
  getDevices: (
    typeId: string | null,
    brandId: string | null,
    limit: number,
    page: number | null
  ) => void;
  getTypes: () => void;
  setTypeId: (data: string | null) => setTypeIdActionType;
  setBrandId: (data: string | null) => setBrandIdActionType;
  setCurrentPage: (data: number) => setCurrentPageActionType;
};
type PropsType = MapDispathPropsType & MapStateToPropsType;
//-----------------------------------------

const Content: React.FC<PropsType> = ({
  getDevices,
  getTypes,
  setTypeId,
  setBrandId,
  setCurrentPage,
  devices,
  types,
  totalCount,
  limit,
  page,
  typeId,
  brandId,
}) => {
  let countPage: number = Math.ceil(totalCount / limit); //для пагинации,расчёт количества страниц

  useEffect(() => {
    //если число страниц меньше активной страницы
    if (totalCount / limit < page) {
      setCurrentPage(1);
    }
    getDevices(typeId, brandId, limit, page);

    // eslint-disable-next-line
  }, [typeId, brandId, page]);
  useEffect(() => {
    getTypes();

    // eslint-disable-next-line
  }, []);
  return (
    <Grid item container>
      <TypeBar types={types} setTypeId={setTypeId} setBrandId={setBrandId} />
      <DeviceContainer
        devices={devices}
        countPage={countPage}
        page={page}
        setCurrentPage={setCurrentPage}
      />
    </Grid>
  );
};
const mapStateToProps = (state: RootStateType): MapStateToPropsType => {
  return {
    devices: state.devices.devices,
    types: state.devices.types,
    totalCount: state.devices.totalCount,
    limit: state.devices.limit,
    page: state.devices.page,
    typeId: state.devices.typeId,
    brandId: state.devices.brandId,
  };
};
export default connect<
  MapStateToPropsType,
  MapDispathPropsType,
  unknown, // личные пропсы
  RootStateType
>(mapStateToProps, {
  getDevices,
  getTypes,
  setTypeId,
  setBrandId,
  setCurrentPage,
})(Content);
