import React from 'react';
import {
  Typography,
  Divider,
  createStyles,
  makeStyles,
  Grid,
  Box,
} from '@material-ui/core';
import { DeviceType } from '../store/reducer/deviceReducer';
import { ROOT_URL } from '../constants/url';
import { useHistory } from 'react-router-dom';

//типизация----------------------

type PropsType = {
  item: DeviceType; //типизация  выбранного устройства
};
//---------------------------------

const useStyles = makeStyles(() =>
  createStyles({
    root: {
      padding: '15px 0px 0px 15px',
    },

    media: {
      height: 100,
      cursor: 'pointer',
      paddingTop: 50,
    },
    lower_typography: {
      cursor: 'pointer',
      marginRight: '50px',
      color: '#0047ae',
      '&:hover': {
        color: '#01579b',
      },
    },
  })
);

const Device: React.FC<PropsType> = ({ item }) => {
  const { name, picture, price, description } = item;
  const classes = useStyles();
  const history = useHistory();

  return (
    <div>
      <Grid container spacing={2} className={classes.root}>
        <Grid
          item
          md={3}
          className={classes.media}
          onClick={() => {
            history.push(`/profileDevice/${item._id}`);
          }}
        >
          <img
            src={`${ROOT_URL}/${picture[0]}`}
            style={{ height: '100px', width: 'auto' }}
          />
        </Grid>
        <Grid item md={8}>
          <Box style={{ display: 'flex', flexDirection: 'column' }}>
            <Typography component="h5" variant="h5" color="initial">
              {name}
            </Typography>
            <Typography variant="subtitle1" color="inherit">
              {price} p
            </Typography>
            <Typography
              variant="subtitle2"
              color="textSecondary"
              style={{ wordWrap: 'break-word' }}
            >
              {description && description}
            </Typography>
            <Box display="flex" marginTop={2}>
              <Typography
                variant="subtitle2"
                className={classes.lower_typography}
                onClick={() => {
                  history.push(`/profileDevice/${item._id}`);
                }}
              >
                Описание
              </Typography>
              <Typography
                variant="subtitle2"
                className={classes.lower_typography}
              >
                Добавить в карзину
              </Typography>
            </Box>
          </Box>
        </Grid>
      </Grid>

      <Divider />
    </div>
  );
};

export default Device;
