import React from 'react';
import { useForm, SubmitHandler, Controller } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import { MenuItem } from '@material-ui/core';
import { InputLabel } from '@material-ui/core';
import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';
import { useHistory } from 'react-router-dom';
import { TypeDeviceType, BrandType } from '../store/reducer/deviceReducer';

//типизация данных
type InfoType = {
  title: string;
  discription: string;
};

type DataDeviceType = {
  name: string;
  price: number;
  picture: any;
  info: InfoType[];
  typeId: string;
  brandId: string;
};

//схема валидации---------------------
const schema = yup.object().shape({});
//-----------------------------------------
// типизация пропсов
type PropsType = {
  types: TypeDeviceType[];
  brands: BrandType[];
};
//--------------------------------------------

const useStyles = makeStyles((theme) => ({
  form: {
    width: '100%',
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  link: {
    pointerEvents: 'none',
  },
}));

const FormDevice: React.FC<PropsType> = ({ types, brands }) => {
  const history: any = useHistory();
  const classes = useStyles();
  const {
    handleSubmit,
    control,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
    mode: 'onChange',
  });
  // получение данных из формы и отправка на сервак(авторизация или регистрация)
  const onSubmit: SubmitHandler<DataDeviceType> = (data: DataDeviceType) =>
    void {};

  return (
    <form className={classes.form} noValidate onSubmit={handleSubmit(onSubmit)}>
      <Grid container>
        <Grid item xs={12} sm={3}>
          <Controller
            name="types"
            control={control}
            defaultValue=""
            render={({ field }) => (
              <TextField
                {...field}
                style={{ color: '#1a237e' }}
                fullWidth
                error={!!errors.types}
                select
                margin="normal"
                label="Выберите тип "
                variant="outlined"
                helperText={errors.types ? errors.types?.message : null}
              >
                {types.map((item, index) => {
                  return (
                    <MenuItem value={item._id} key={index}>
                      {item.name}
                    </MenuItem>
                  );
                })}
              </TextField>
            )}
          />
        </Grid>
        <Grid item xs={false} sm={6}></Grid>
        <Grid item xs={12} sm={3}>
          nsxkjdnmklmnjcdksvmdfklbmlkghknklghk lhjkml;hjk ml;jm l;hj,
          l;jlk;ljk;k;jk
        </Grid>
      </Grid>
    </form>
  );
};
export default FormDevice;
