import React, { useState } from 'react';
import { useForm, SubmitHandler, Controller } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import IconButton from '@material-ui/core/IconButton';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import Button from '@material-ui/core/Button';
import { title } from 'process';
import { ListItem } from '@material-ui/core';

//типизация данных
type InfoType = {
  title: string;
  description: string;
  id?: number;
};

//схема валидации---------------------
const schema = yup.object().shape({
  title: yup.string().required('Поле обязательное для заполнения'),
  description: yup.string().required('Поле обязательное для заполнения'),
});
//-----------------------------------------

const DeviceProperty = () => {
  const [info, setInfo] = useState<InfoType[]>([]);

  const addInfo = (): void => {
    setInfo([...info, { title: '', description: '', id: Date.now() }]);
  };

  const removeInfo = (id: number | undefined): void => {
    setInfo(info.filter((item) => item.id !== id));
  };

  const {
    handleSubmit,
    control,
    watch,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
    mode: 'onBlur',
  });
  // получение данных из формы и отправка на сервак(авторизация или регистрация)
  const onSubmit: SubmitHandler<InfoType[]> = (data: InfoType[]): void => {
    console.log('Отправлено:', data);
  };
  const title = watch('title');
  console.log(title);

  return (
    <React.Fragment>
      <Button
        color="primary"
        style={{ marginBottom: 15 }}
        onClick={addInfo}
        //className={classes.button}
      >
        Добавить новое свойство
      </Button>
      <form noValidate onSubmit={handleSubmit(onSubmit)}>
        {info.map((item) => {
          return (
            <Grid container spacing={3} key={item.id}>
              <Grid item xs={12} sm={3}>
                <Controller
                  name="title"
                  control={control}
                  defaultValue=""
                  render={({ field }) => (
                    <TextField
                      {...field}
                      required
                      fullWidth
                      id="title"
                      label=" название "
                      autoFocus
                      error={!!errors.title}
                      helperText={errors.title ? errors.title?.message : null}
                    />
                  )}
                />
              </Grid>
              <Grid item xs={12} sm={7}>
                <Controller
                  name="description"
                  control={control}
                  defaultValue=""
                  render={({ field }) => (
                    <TextField
                      {...field}
                      required
                      fullWidth
                      id="description"
                      label=" описание "
                      multiline
                      error={!!errors.description}
                      helperText={
                        errors.description ? errors.description?.message : null
                      }
                    />
                  )}
                />
              </Grid>
              <Grid item xs={12} sm={2}>
                <IconButton onClick={() => removeInfo(item.id)}>
                  <HighlightOffIcon style={{ fontSize: 30 }} />
                </IconButton>
              </Grid>
            </Grid>
          );
        })}
        <Button type="submit">Отправить</Button>
      </form>
    </React.Fragment>
  );
};
export default DeviceProperty;
