import React, { useState } from 'react';
import { makeStyles, Theme } from '@material-ui/core/styles';
import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Paper from '@material-ui/core/Paper';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    marginTop: 20,
    border: '1px solid',
    backgroundColor: theme.palette.background.paper,
  },
}));

const TypeBar: React.FC = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(false); // для вложенного списа
  const [indexActiv, setIndexActiv] = useState<null | number>(null); //чтобы не раскрывался весь список

  const handleClick = (index: number | null) => {
    setIndexActiv(index);
    if (indexActiv === index) {
      setOpen(!open);
    } else {
      setOpen(false);
    }
  }; // управление вложенным списком

  console.log(indexActiv);
  const brand = ['Samsung', 'Lenovo', 'Acer'];
  const device = ['Смартфоны', 'Планшеты', 'Ноутбуки'];
  return (
    <List
      dense={true}
      className={classes.root}
      // component="nav"
      // subheader={<ListSubheader component="div">Device List</ListSubheader>}
    >
      {device.map((element, index) => {
        return (
          <Box key={element}>
            <ListItem button onClick={() => handleClick(index)}>
              <ListItemText
                inset={true}
                disableTypography={false}
                primary={
                  <Typography variant="body2" style={{ color: '#ff6f00' }}>
                    {element}
                  </Typography>
                }
              />
            </ListItem>

            <Collapse
              in={indexActiv === index && open}
              timeout="auto"
              unmountOnExit
            >
              <List component="div">
                {brand.map((el) => {
                  return (
                    <ListItem button key={el}>
                      <ListItemText inset primary={el} />
                    </ListItem>
                  );
                })}
              </List>
            </Collapse>
          </Box>
        );
      })}
    </List>
  );
};

export default TypeBar;
