import React from 'react';
import { makeStyles, createStyles } from '@material-ui/core/styles';
import { setCurrentPageActionType } from '../store/reducer/deviceReducer';
import Pagination from '@material-ui/lab/Pagination';

// типизация пропсов
type PropsType = {
  setCurrentPage: (data: number) => setCurrentPageActionType;
  totalCount: number;
  limit: number;
};

const useStyles = makeStyles((theme) =>
  createStyles({
    root: {
      '& > *': {
        marginTop: theme.spacing(2),
      },
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'center',
      marginBottom: theme.spacing(2),
    },
  })
);

const PaginationButtons: React.FC<PropsType> = ({
  totalCount,
  limit,
  setCurrentPage,
}) => {
  const classes = useStyles();
  let countPage: number = Math.ceil(totalCount / limit);
  // console.log(countPage);
  const handleChange = (event: React.ChangeEvent<unknown>, value: number) => {
    setCurrentPage(value);
  };

  return (
    <div className={classes.root}>
      <Pagination
        count={countPage}
        showFirstButton
        showLastButton
        onChange={handleChange}
      />
    </div>
  );
};
export default PaginationButtons;
