import React from 'react';
import { useHistory } from 'react-router';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import {
  TypeDeviceType,
  setTypeIdActionType,
  setBrandIdActionType,
} from '../store/reducer/deviceReducer'; //типизация
import byk1 from '../img/byk1.jpg';
import planshet1 from '../img/planshet1.jpg';
import reclama1 from '../img/reclama1.jpg';

//----типизация пропсов----
type PropsType = {
  types: TypeDeviceType[];
  setTypeId: (data: string | null) => setTypeIdActionType;
  setBrandId: (data: string | null) => setBrandIdActionType;
};
//-------------------------

const TypeBar: React.FC<PropsType> = ({ types, setTypeId, setBrandId }) => {
  const history = useHistory(); //для изменения строки запроса
  //запись выбранного типа устройства в стейт и удаление существующего брэнда из стейта и маршрутизация
  const handleDevice = (id: string) => {
    history.push(`/profileType/${id}`);
    setTypeId(id);
    setBrandId(null);
  };

  return (
    <>
      <Typography style={{ marginTop: '115px', fontWeight: 'bold' }}>
        {' '}
        Каталог товаров
      </Typography>
      <List component="nav" aria-label="main mailbox folders">
        {types &&
          types.map((type) => {
            return (
              <div key={type._id}>
                <ListItem
                  button
                  onClick={() => {
                    handleDevice(type._id);
                  }}
                >
                  <ListItemText primary={type.name} />
                </ListItem>
                <Divider />
              </div>
            );
          })}
      </List>
      <div>
        <img
          src={reclama1}
          style={{ height: '200px', width: 'auto', marginTop: '25px' }}
        />
        <img
          src={planshet1}
          style={{ height: '205px', width: 'auto', marginTop: '25px' }}
        />
        <img
          src={byk1}
          style={{ height: '205px', width: 'auto', marginTop: '25px' }}
        />
      </div>
    </>
  );
};

export default TypeBar;
