import React, { useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import { RootStateType } from '../store/store'; // типизация всего стейта( для типизации state)
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import CircularProgress from '@material-ui/core/CircularProgress';
import { Box, Container, Button, Divider } from '@material-ui/core';
import ShoppingCartOutlinedIcon from '@material-ui/icons/ShoppingCartOutlined';
import DeleteForeverOutlinedIcon from '@material-ui/icons/DeleteForeverOutlined';
import ActiveLastBreadcrumb from '../components/ActiveLastBreadcrumb';
import { connect } from 'react-redux';
import { DeleteForeverOutlined } from '@material-ui/icons';

//типизация---------------------------------------------------------------------
type MapStateToPropsType = {};
type MapDispathPropsType = {};
type PropsType = MapStateToPropsType & MapDispathPropsType;

//-----------------------------------------------------------------
const useStyles = makeStyles((theme) => ({
  breadcrumb: {
    marginTop: 15,
  },
  shoppingCartOutlinedIcon: {
    fontSize: '24px',
    position: 'relative',
    top: '3px',
    right: '10px',
  },
  deleteCart: {
    display: 'flex',
    position: 'relative',
    top: '10px',
    cursor: 'pointer',
    '&:hover': {
      color: '#000',
    },
  },
  deleteForeverOutlinedIcon: {
    fontSize: '16px',
    position: 'relative',
    top: '5px',
    right: '5px',
  },
  textTitle: {
    marginTop: '50px',
    marginBottom: '50px',
  },
  root: {
    padding: '15px 0px 0px 15px',
  },

  media: {
    height: 100,
    cursor: 'pointer',
    paddingTop: 50,
  },
}));

const Cart: React.FC<PropsType> = ({}) => {
  const classes = useStyles();

  useEffect(() => {
    // eslint-disable-next-line
  }, []);

  return (
    <Container maxWidth="lg">
      <Box className={classes.breadcrumb}>
        <ActiveLastBreadcrumb name="Корзина" />
      </Box>
      <Container maxWidth="sm">
        <Box
          display="flex"
          marginTop={5}
          marginBottom={5}
          justifyContent="space-between"
        >
          <Typography
            component="h1"
            variant="h5"
            align="center"
            style={{
              display: 'flex',
            }}
          >
            <ShoppingCartOutlinedIcon
              className={classes.shoppingCartOutlinedIcon}
            />{' '}
            Корзина
          </Typography>
          <Typography color="textPrimary">
            всего товаров: <span style={{ fontWeight: 'bold' }}>3</span>
          </Typography>
        </Box>
        {false ? (
          <Typography
            align="center"
            color="error"
            className={classes.textTitle}
          >
            Что-то пошло не так!
          </Typography>
        ) : false ? (
          <Box
            display="flex"
            justifyContent="center"
            alignItems="center"
            style={{ paddingTop: '50px' }}
          >
            <CircularProgress />
          </Box>
        ) : [1].length === 0 ? (
          <Typography align="center" className={classes.textTitle}>
            Ваша корзина пуста!
          </Typography>
        ) : (
          <>
            <Box style={{ display: 'flex', flexDirection: 'column' }}>
              {[] &&
                [1].map((item) => {
                  return (
                    <>
                      <Grid container spacing={2} className={classes.root}>
                        <Grid
                          item
                          md={3}
                          className={classes.media}
                          /*  onClick={() => {
                        history.push(`/profileDevice/${item._id}`);
                      }} */
                        >
                          <img
                            // src={`${ROOT_URL}/${picture[0]}`}
                            style={{ height: '100px', width: 'auto' }}
                            alt="картинка"
                          />
                        </Grid>
                        <Grid item md={8}>
                          <Box>
                            <Typography
                              component="h5"
                              variant="h5"
                              color="initial"
                            >
                              Samsung Galaxy Note9 128Gb
                            </Typography>
                            <Typography variant="subtitle1" color="inherit">
                              1450 p
                            </Typography>
                            <Typography
                              variant="subtitle2"
                              color="textSecondary"
                              style={{ wordWrap: 'break-word' }}
                            >
                              2 SIM, Android, экран 6.4 ", Super AMOLED,
                              1440x2960, Exynos 9810, оперативная память 6 Гб,
                              встроенная память 128 Гб, камера 12 Мп,
                              аккумулятор 4000 мАч, цвет синий.
                            </Typography>
                          </Box>
                        </Grid>
                      </Grid>
                      <Divider />
                    </>
                  );
                })}
            </Box>
          </>
        )}
        <Box
          display="flex"
          marginTop={5}
          marginBottom={5}
          justifyContent="space-between"
        >
          <Typography variant="h6">сумма заказа:1200 р</Typography>
          <Button style={{ backgroundColor: '#0047ae', color: '#e0e0e0' }}>
            Оформить заказ
          </Button>
        </Box>
      </Container>
    </Container>
  );
};

const mapStateToProps = (state: RootStateType): MapStateToPropsType => {
  return {};
};
export default connect<
  MapStateToPropsType,
  MapDispathPropsType,
  unknown, // первичные пропсы
  RootStateType
>(
  mapStateToProps,
  {}
)(Cart);
