import React from 'react';

import { DeviceType } from '../store/reducer/deviceReducer';
import { ROOT_URL } from '../constants/url';
import { useHistory } from 'react-router-dom';

import {
  Theme,
  createStyles,
  makeStyles,
  useTheme,
} from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import { Button, CardActions, Divider } from '@material-ui/core';

//типизация----------------------

type PropsType = {
  item: DeviceType;
  removeDevice: (id: string | undefined) => void;
};
//---------------------------------

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: 'flex',
      padding: 15,
    },

    content: {
      flex: '1 0 auto',
    },

    media: {
      height: 150,
      cursor: 'pointer',
    },
  })
);

const DeleteDevice: React.FC<PropsType> = ({ item, removeDevice }) => {
  const { name, picture, price } = item;
  const classes = useStyles();
  const theme = useTheme();
  const history = useHistory();

  return (
    <div key={item._id}>
      <div className={classes.root}>
        <div
          className={classes.media}
          onClick={() => {
            history.push(`/profileDevice/${item._id}`);
          }}
        >
          <img
            src={`${ROOT_URL}/${picture[0]}`}
            style={{ height: '100px', width: 'auto' }}
          />
        </div>

        <div className={classes.content}>
          <Typography component="h5" variant="h5">
            Live From Space
          </Typography>
          <Typography variant="subtitle1" color="textSecondary">
            Mac Miller
          </Typography>
        </div>
      </div>

      <Button
        variant="outlined"
        color="secondary"
        fullWidth={false}
        onClick={() => removeDevice(item._id)}
      >
        Удалить
      </Button>

      <Divider />
    </div>
  );
};

export default DeleteDevice;

