import React from 'react';
import { useForm, Controller } from 'react-hook-form';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import { Divider } from '@material-ui/core';
import Button from '@material-ui/core/Button';
import Modal from '@material-ui/core/Modal';

//-----------------------------------------
// типизация пропсов
type PropsType = {
  handleClose: () => void;
  open: boolean;
  title: string;
};
//--------------------------------------------
//схема валидации---------------------
const schema = yup.object().shape({
  name: yup.string().required('Поле обязательное для заполнения'),
});
//-----------------------------------------
const useStyles = makeStyles((theme) => ({
  paper: {
    // position: 'absolute',
    width: 450,
    backgroundColor: theme.palette.background.paper,
    // border: '2px solid #000',
    boxShadow: theme.shadows[5],
    // padding: theme.spacing(2, 4, 3),
  },
  modal: {
    display: 'flex',
    //padding: theme.spacing(1),
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttons: {
    display: 'flex',
    justifyContent: 'flex-end',
  },
}));

const ModalWindow: React.FC<PropsType> = ({ handleClose, open, title }) => {
  const classes = useStyles();
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({
    mode: 'onBlur',
    resolver: yupResolver(schema),
  });

  const onSubmit = (data: any) => {
    console.log(data);
  };

  return (
    <Modal open={open} className={classes.modal}>
      <div className={classes.paper}>
        <Typography component="h1" variant="h6" style={{ padding: 15 }}>
          Добавить {title}
        </Typography>
        <Divider />
        <form style={{ padding: 15 }} onSubmit={handleSubmit(onSubmit)}>
          <Controller
            name="name"
            control={control}
            defaultValue=""
            render={({ field }) => (
              <TextField
                {...field}
                // variant="outlined"
                margin="normal"
                required
                fullWidth
                label="Введите название типа"
                error={!!errors.name}
                helperText={errors.name ? errors.name?.message : null}
              />
            )}
          />
          <div className={classes.buttons}>
            <Button
              variant="outlined"
              color="primary"
              style={{ margin: 5, fontSize: 12 }}
              onClick={handleClose}
            >
              Закрыть
            </Button>
            <Button
              type="submit"
              variant="outlined"
              color="primary"
              style={{ margin: 5, fontSize: 12 }}
              onClick={() => {}}
            >
              Добавить
            </Button>
          </div>
        </form>
      </div>
    </Modal>
  );
};

export default ModalWindow;
