import React, { useState } from 'react';
import { makeStyles, Theme } from '@material-ui/core/styles';
import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Collapse from '@material-ui/core/Collapse';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme: Theme) => ({
  /*  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  }, */
  /* nested: {
    paddingLeft: theme.spacing(4),
  }, */
}));

const TypeBar: React.FC = () => {
  const classes = useStyles();
  const [open, setOpen] = useState(true);
  const [indexActiv, setIndexActiv] = useState<null | number>(null);

  const handleClick = (index: number | null) => {
    setOpen(!open);
    setIndexActiv(index);
  };
  const handleBrand = (e: React.MouseEvent<HTMLDivElement>) => {
    e.preventDefault();
  };
  console.log(indexActiv);
  const brand = ['Samsung', 'Lenovo', 'Acer'];
  const device = ['Смартфоны', 'Планшеты', 'Ноутбуки'];
  return (
    <List
    // component="nav"
    // subheader={<ListSubheader component="div">Device List</ListSubheader>}
    >
      {device.map((element, index) => {
        return (
          <div key={element}>
            <ListItem button onClick={() => handleClick(index)}>
              <ListItemText inset primary={element} />
            </ListItem>
            <Collapse
              in={indexActiv === index && open}
              timeout="auto"
              unmountOnExit
            >
              <List component="div">
                {brand.map((el) => {
                  return (
                    <ListItem button key={el} onClick={(e) => handleBrand(e)}>
                      <ListItemText inset primary={el} />
                    </ListItem>
                  );
                })}
              </List>
            </Collapse>
          </div>
        );
      })}
    </List>
  );
};

export default TypeBar;
