import React, { useState } from 'react';
import { makeStyles, Theme } from '@material-ui/core/styles';
import ListSubheader from '@material-ui/core/ListSubheader';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import Typography from '@material-ui/core/Typography';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ExpandLess from '@material-ui/icons/ExpandLess';
import ExpandMore from '@material-ui/icons/ExpandMore';
import Collapse from '@material-ui/core/Collapse';
import {
  TypeDeviceType,
  setTypeIdActionType,
  setBrandIdActionType,
} from '../store/reducer/deviceReducer'; //типизация
import { Grid } from '@material-ui/core';

//----типизация пропсов----
type PropsType = {
  types: TypeDeviceType[];
  setTypeId: (data: string) => setTypeIdActionType;
  setBrandId: (data: string | null) => setBrandIdActionType;
};
//-------------------------

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    marginTop: 50,
  },
  listType: { paddingLeft: theme.spacing(5) },
  activListType: { paddingLeft: theme.spacing(5), backgroundColor: '#eeeeee' },
  listBrand: {
    paddingLeft: theme.spacing(9),
    color: '#9e9e9e',
  },
  activListBrand: {
    color: theme.palette.primary.main,
  },
}));

const TypeBar: React.FC<PropsType> = ({ types, setTypeId, setBrandId }) => {
  const classes = useStyles();
  //создаём объект с булевыми значениями для управления элементами списка(открытие закрытие)
  let objType = {} as any;
  for (let i = 0; i < types.length; i++) {
    objType[i] = false;
    // console.log(obj);
  }

  const [open, setOpen] = useState(objType); // открытие вложенного списа
  const [activType, setActivType] = useState<null | number>(null); // выбор типа
  const [activBrand, setActivBrand] = useState<null | string>(null); //выбор бренда

  // выбор типа,открытие  вложенного спиская,снятие выбранного брэнда
  const handleType = (index: number) => {
    setActivType(index);
    open[index] = !open[index];
    setOpen({ ...open });
    setActivBrand(null); // снятие выбранного бренда
  }; // выбор брэнда
  const handleBrand = (id: string, index: number) => {
    setActivBrand(id);
    setActivType(index);
  };

  return (
    <Grid item xs={12} sm={2}>
      <List
        component="nav"
        className={classes.root}
        subheader={<ListSubheader component="div">Тип устройств</ListSubheader>}
      >
        {types.map((item, index) => {
          return (
            <div key={item._id}>
              <ListItem
                className={
                  activType === index ? classes.activListType : classes.listType
                }
                button
                onClick={() => {
                  handleType(index);
                  setTypeId(item._id); //запись в стейт выбранного типа
                  setBrandId(null); // удаление из стейта существующего брэнда
                }}
              >
                <ListItemText
                  disableTypography
                  primary={
                    <Typography variant="subtitle1" gutterBottom>
                      {item.name}
                    </Typography>
                  }
                />
                {open[index] ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Divider />
              <Collapse in={open[index]} timeout="auto" unmountOnExit>
                <List component="div">
                  {item.brands.map((item) => {
                    return (
                      <div key={item._id}>
                        <ListItem
                          button
                          className={classes.listBrand}
                          onClick={() => {
                            handleBrand(item._id, index);
                            setTypeId(item._id); //запись в стейт выбранного типа
                            setBrandId(item._id); //запись в стейт выбранного брэнда
                          }}
                        >
                          <ListItemText
                            disableTypography
                            className={
                              activBrand === item._id && activType === index
                                ? classes.activListBrand
                                : undefined
                            }
                            primary={
                              <Typography variant="subtitle2" gutterBottom>
                                {item.name}
                              </Typography>
                            }
                          />
                        </ListItem>
                        <Divider />
                      </div>
                    );
                  })}
                </List>
              </Collapse>
            </div>
          );
        })}
      </List>
    </Grid>
  );
};

export default TypeBar;
